

def direction(symbol):
    if symbol == "(":
        return 1
    elif symbol == ")":
        return -1
    else:
        raise ValueError(f"Unsupported symbol: {symbol}")




def floor_calculation_loop(input):
    calculation = 0
    for parens in input:
        calculation += direction(parens)
    return calculation

def find_first_negative(input):
    index_counter = 0
    current_level = 0
    for parens in input:
        index_counter += 1
        current_level += direction(parens)
        if current_level < 0:
            break
    return index_counter


